 customer_name : function(frm){
            // Triggered when the customer field is changed
        // Get the selected customer's salesperson
         frappe.call({
            method: 'frappe.client.get_list',
            args: {
                doctype: 'Customer',
                filters: {
                    name: frm.doc.party_name, // Get Sales Teams related to this Customer
                },
                fields: ['sales_team.sales_person'],
            },
            callback: function(r) {
                if (r.message && r.message.length > 0) {
                    // Assuming there can be multiple Sales Team entries for a customer
                    // You can handle multiple entries as needed
                    const salesPerson = r.message[0].sales_person;
                    // Do something with the salesPerson value, e.g., display it in a field
                    frm.set_value('custom_sales_person', salesPerson);
                }
            }
        });


    }
})

