function chkWarehouse(frm) {

    frm.set_query('warehouse', 'items', function (doc, cdt, cdn) {
        var chk = frm.doc.__unsaved ? frm.doc.cost_center : '';
        var filters = erpnext.queries.warehouse(frm.doc);
        if (chk === undefined) {
                frappe.msgprint('Please specify: Branch. It is needed to fetch Item Details.')
    }

        var d = locals[cdt][cdn];
        if (chk) {
            $.extend(filters, {
                "query": "erpnext.controllers.queries.warehouse_query",
                "filters": [
                    ["Warehouse", "parent_warehouse", "=", chk],
                    ["Bin","item_code","=",d.item_code]
                    ]
            });
        }        
        return filters;
    });

    frm.set_query('set_warehouse', function (doc, cdt, cdn) {

        var d = locals[cdt][cdn];
        return {
            "filters": {
                "parent_warehouse": chk,
                "is_group":0
            }
        };
    });
}

